(function($){var take=function(e,num){var arrProto=Array.prototype;if(num>this.length||num>e.length){return false};for(var i=0;i<num;i++){arrProto.push.call(this,arrProto.shift.call(e));arrProto.push.call(e,arrProto.shift.call(this));};return this;};$.fn.carouse=function(num){var element=$(this),children=element.children(),active=children.slice(0,num),inactive=children.slice(num);children.addClass("frame");active.addClass("carouseKids");$(element).on("click",function(e){if(e.offsetX<element.offset().left){take.call(active,Array.prototype.reverse.call(inactive),num);inactive.removeClass("carouseKids");active.addClass("carouseKids");Array.prototype.reverse.call(inactive);}else{take.call(active,inactive,num);inactive.removeClass("carouseKids");active.addClass("carouseKids");}});};})(jQuery);